package main

import "testing"

func TestFindLongest1s(t *testing.T) {
	tests := []struct {
		description string
		input       string
		output      int
	}{
		{
			"2 bits",
			"00",
			1,
		},
		{
			"5 bits",
			"01010",
			3,
		},
		{
			"11 bits",
			"11011101111",
			8,
		},
		{
			"50 bits",
			"01110100110011000101001100011110010010101011111011",
			8,
		},
		{
			"100 bits",
			"1011010111110110111001001110111011000001011010110011101101111101111111011100011001101011011110001010",
			13,
		},
		{
			"999 bits",
			"",
			23,
		},
		{
			"32 bits",
			"10000000011100000000011111000001",
			6,
		},
		{
			"20 bits",
			"00101001100011001111",
			5,
		},
		{
			"the whole string",
			"111111101111111",
			15,
		},
		{
			"all zeros",
			"00000000000000000000",
			1,
		},
		{
			"gaps",
			"1110011100111",
			4,
		},
		{
			"random 100",
			"0010000011000101001110010011011001000111010010101111000011011110001000000000100010100011010100011101",
			7,
		},
		{
			"random 32",
			"00010111111001000101011111111010",
			10,
		},
		{
			"gaps 2",
			"110110011100111",
			5,
		},
		{
			"steps",
			"101101110111101111101111110",
			12,
		},
	}

	for _, test := range tests {
		t.Run(test.description, func(t *testing.T) {
			got := findLongest1s(test.input)
			want := test.output
			if got != want {
				t.Errorf("got: %d, but want: %d, given: %s", got, want, test.input)
			}
		})
	}
}
